sourcesContent:
require('dotenv').config();
import nodemailer from 'nodemailer';
let sendSimpleEmail = async(dataSend) => {
    let transporter = nodemailer.createTransport({
        host: "smtp.gmail.com",
        port: 587,
        secure: false,
        service: 'gmail',
        auth: {
            user: process.env.EMAIL_APP,
            pass: process.env.EMAIL_APP_PASSWORD,
        },
    });
    let info = await transporter.sendMail({
        from:'"Phc vnua" <baophucta2k@gmail.com>',
        to: dataSend.receiverEmail,
        subject: "Thng tin t lch khm bnh cho cy",
        html: getBodyHTMLEmail(dataSend),
        // `<h3>Xin cho ${dataSend.patientName}!</h3>
        // <p>Bn nhn c th ny v  t lch khm bnh online trn web khamchuabenhchocayvnua</p>
        // <p>Thng tin t lch khm bnh:</p>
        // <div><b>Thi gian: ${dataSend.time}</b></div>
        // <div><b>Bc s: ${dataSend.doctorName}</b></div>
        // <div><b>Chun on bnh s b ban u: ${dataSend.diagnosis}</b></div>
        // <p>Nu cc thng tin trn l ng s tht, vui lng click vo ng link bn di
        //  xc nhn v hon tt th tc t lch khm bnh.</p>
        // <div>
        // <a href=${dataSend.redirectLink} target="_blank">Click here</a>
        // </div>
        // <div>Xin chn thnh cm n</div>`,


    });
}
let getBodyHTMLEmail = (dataSend) => {
    let result = ''
    if(dataSend.language === 'vi'){
        result =
        `
        <h3> Xin cho ${ dataSend.patientName } !</h3 >
        <p>Bn nhn c th ny v  t lch khm bnh online trn web khamchuabenhchocayvnua</p>
     <p>Thng tin t lch khm bnh:</p>
        <div><b>Thi gian: ${dataSend.time}</b></div>
        <div><b>Bc s: ${dataSend.doctorName}</b></div>
        <div><b>L do ca khch hng a ra: ${dataSend.reasons}</b></div>
        <p>Nu cc thng tin trn l ng s tht, vui lng click vo ng link bn di
         xc nhn v hon tt th tc t lch khm bnh.</p>
        <div>
        <a href=${dataSend.redirectLink} target="_blank">Click here</a>
        </div>
<div>Bn c th qun l lch khm bnh cho cy ca mnh qua mc qun l ti khon sau khi xc nhn lch khm bnh cho cy</div>
         <div>Xin chn thnh cm n</div>
         `
        
    }
    if (dataSend.language === 'en') {
        result =
            `
        <h3> Hello ${dataSend.patientName} !</h3 >
        <p>You received this message because you booked an online medical appointment on web khamchuabenhchocayvnua</p>
     <p>Information to book a medical appointment:</p>
        <div><b>Time: ${dataSend.time}</b></div>
        <div><b>Doctor: ${dataSend.doctorName}</b></div>
        <div><b>Reasons from customer: ${dataSend.reasons}</b></div>
        <p>If the above information is correct, please click on the link below to confirm and complete the medical appointment booking procedure.</p>
        <div>
        <a href=${dataSend.redirectLink} target="_blank">Click here</a>
        </div>
        <div>You can manage your tree's medical schedule through the account management section after confirming the schedule of medical examination for the tree</div>
         <div>Thank you</div>
         `

    }
    return result;
}
let getBodyHTMLEmailRemedy = (dataSend) => {
    let result = ''
    if (dataSend.language === 'vi') {
        result =
            `
        <h3> Xin cho ${dataSend.patientName} !</h3 >
        <p>Bn nhn c th ny v  t lch khm bnh online trn web khamchuabenhchocayvnua</p>
     <p>Thng tin t lch khm bnh/ ha n  c gi trong file nh km</p>
       
         <div>Xin chn thnh cm n!</div>

         `

    }
    if (dataSend.language === 'en') {
        result =
            `
        <h3> Hello ${dataSend.patientName} !</h3 >
        <p>You received this message because you booked an online medical appointment on web khamchuabenhchocayvnua</p>
     <p>Information to book a medical appointment/Invoice has been sent in attachment</p>
        
         <div>Thank you!</div>
         `

    }
    return result;
}
let getBodyHTMLEmailCancelRemedy = (dataSend) => {
    let result = ''
    if (dataSend.language === 'vi') {
        result =
            `
        <h3> Xin cho ${dataSend.patientName} !</h3 >
        <p>Bn nhn c th ny v  t lch khm bnh online trn web khamchuabenhchocayvnua</p>
     <p>Rt tic nhng hin ti bc s/phng khm ca chng ti khng th x l yu cu khm bnh ca qu khch ng hn. Qy khch c th t lch khm bnh cho cy vo thi im hoc bc s khc c trn website. Mong qu khch thng cm</p>
     <p>L do: Bc s  kn lch hn vo thi gian ny</p>
       <div>Bn c th qun l lch khm bnh cho cy ca mnh qua mc qun l ti khon</div>
         <div>Xin chn thnh cm n!</div>

         `

    }
    if (dataSend.language === 'en') {
        result =
            `
        <h3> Hello ${dataSend.patientName} !</h3 >
        <p>You received this message because you booked an online medical appointment on web khamchuabenhchocayvnua</p>
     <p>We're sorry, but our doctor/clinic is currently unable to process your request on time. You can schedule a medical examination for the tree at a time or another doctor available on the website. Wish you sympathize</p>
       <p>Reason: The doctor is full of appointments at this time</p>
        <div>You can manage your tree's medical schedule through the account management section</div>
         <div>Thank you!</div>
         `

    }
    return result;
}
let getBodyHTMLEmailCancelPatientRemedy = (dataSend) => {
    let result = ''
    if (dataSend.language === 'vi') {
        result =
            `
        <h3> Xin cho ${dataSend.patientName} !</h3 >
        <p>Bn nhn c th ny v  t lch khm bnh online trn web khamchuabenhchocayvnua</p>
     <p>Bn  hy lch hn khm thnh cng! Rt mong c phc v bn ln sau!</p>
     
       <div>Bn c th qun l lch khm bnh cho cy ca mnh qua mc qun l ti khon</div>
         <div>Xin chn thnh cm n!</div>

         `

    }
    if (dataSend.language === 'en') {
        result =
            `
        <h3> Hello ${dataSend.patientName} !</h3 >
        <p>You received this message because you booked an online medical appointment on web khamchuabenhchocayvnua</p>
     <p>You have successfully canceled your appointment! Looking forward to serving you next time!</p>
     
        <div>You can manage your tree's medical schedule through the account management section</div>
         <div>Thank you!</div>
         `

    }
    return result;
}
let sendAttachment = async (dataSend) => {
    return new Promise(async (resolve, reject)=> {
        try{
            let transporter = nodemailer.createTransport({
                host: "smtp.gmail.com",
                port: 587,
                secure: false,
                auth: {
                    user: process.env.EMAIL_APP,
                    pass: process.env.EMAIL_APP_PASSWORD,
                }
            });
            let info = await transporter.sendMail({
                from: '"Team T Bo Phc" <baophucta2k@gmail.com>',
                to: dataSend.email,
                subject: "Dear",
                html: getBodyHTMLEmailRemedy(dataSend),
                attachments: [
                    {
                        filename: `remedy-${dataSend.patientId}-${new Date().getTime()}.png`,
                        content: dataSend.imgBase64.split("base64,")[1],
                        encoding: 'base64'
                    }
                ]
            })
resolve(true)
        }catch(e){
            reject(e)

        }
    })
}
let sendCancelAttachment = async (dataSend) => {
    return new Promise(async (resolve, reject) => {
        try {
            let transporter = nodemailer.createTransport({
                host: "smtp.gmail.com",
                port: 587,
                secure: false,
                auth: {
                    user: process.env.EMAIL_APP,
                    pass: process.env.EMAIL_APP_PASSWORD,
                }
            });
            let info = await transporter.sendMail({
                from: '"Team T Bo Phc" <baophucta2k@gmail.com>',
                to: dataSend.email,
                subject: "Dear",
                html: getBodyHTMLEmailCancelRemedy(dataSend)
            })
            resolve(true)
        } catch (e) {
            reject(e)

        }
    })
}
let sendCancelPatientAttachment = async (dataSend) => {
    return new Promise(async (resolve, reject) => {
        try {
            let transporter = nodemailer.createTransport({
                host: "smtp.gmail.com",
                port: 587,
                secure: false,
                auth: {
                    user: process.env.EMAIL_APP,
                    pass: process.env.EMAIL_APP_PASSWORD,
                }
            });
            let info = await transporter.sendMail({
                from: '"Team T Bo Phc" <baophucta2k@gmail.com>',
                to: dataSend.email,
                subject: "Dear",
                html: getBodyHTMLEmailCancelPatientRemedy(dataSend)
            })
            resolve(true)
        } catch (e) {
            reject(e)

        }
    })
}
module.exports = {
    sendSimpleEmail: sendSimpleEmail,
    sendAttachment: sendAttachment,
    sendCancelAttachment: sendCancelAttachment,
    sendCancelPatientAttachment: sendCancelPatientAttachment
}

