import db from "../models/index";
let createClinic = (data) => {
    return new Promise(async(resolve, reject)=> {
        try{
            if(!data.name || !data.address
                || !data.avatar
                || !data.descriptionHTML
                || !data.descriptionMarkdown){
                    resolve({
                        errCode: 1,
                        errMessage: ' Missing parameter '
                    })
                }else{
                    await db.Clinic.create({
                        name: data.name,
                        address: data.address,
                        image: data.avatar,
                        descriptionMarkdown: data.descriptionMarkdown,
                        descriptionHTML: data.descriptionHTML
                    })
                    resolve({
                        errCode: 0,
                        errMessage:'ok'
                    })
                }

        }catch(e){
            reject(e);

        }
    })
}
let getAllClinic = () => {
    return new Promise(async(resolve, reject)=> {
        try{
let data = await db.Clinic.findAll({

});
if(data && data.length > 0){
    data.map(item => {
            item.image = new Buffer(item.image, 'base64').toString('binary');
            return item;
        
    })
   
}
            resolve({
                errMessage: 'ok',
                errCode: 0,
                data
            })

        }catch(e){
            reject(e);

        }
    })
}
let getDetailClinicById = (inputId) => {
    return new Promise(async(resolve, reject)=> {
        try{
            if(!inputId){
                resolve({
                    errCode: 1,
                    errMessage: 'Missing parameter'
                })
            }
            else{
                let data = await db.Clinic.findOne({
                    where:{
                        id: inputId
                    },
                    attributes:['name', 'address', 'descriptionHTML', 'descriptionMarkdown'],
                })
                if(data){
                    let doctorClinic = [];
                    doctorClinic = await db.Doctor_Infor.findAll({
                        where: {clinicId: inputId},
                        attributes: ['doctorId', 'provinceId'],
                    })
                    data.doctorClinic = doctorClinic;
                    
                }else data = {}
                resolve({
                    errMessage: 'ok',
                    errCode: 0,
                    data
                })
            }

        }catch(e){
   
            reject(e);

        }
    })
}

let showAllClinics = (clinicId) => {
    return new Promise(async (resolve, reject) => {
        try {
            let clinics = '';
            if (clinicId === 'ALL') {
                clinics = await db.Clinic.findAll({
                })
            }
            if (clinicId && clinicId !== 'ALL') {
                clinics = await db.Clinic.findOne({
                    where: { id: clinicId },
                    include: [
                        {
                            model: db.Clinic,
                            attributes: ['descriptionMarkdown', 'descriptionHTML']
                        },
                    ],
                })
            }
            resolve(clinics)
            //resolve để thoát ra khỏi Promise 
        } catch (e) {
            reject(e);
        }
    })
}
let deleteClinic = (clinicId) => {
    return new Promise(async (resolve, reject) => {
        let foundClinic = await db.Clinic.findOne({
            where: { id: clinicId }
        })
        if (!foundClinic) {
            resolve({
                errCode: 2,
                errMessage: `The clinic isn't exist`
            })
        }
        await db.Clinic.destroy({
            where: { id: clinicId }
        })
        resolve({
            errCode: 0,
            message: `The user is deleted`
        })
    })
}
let updateClinicData = (data) => {
    return new Promise(async (resolve, reject) => {
        try {
            if (!data.id) {
                resolve({
                    errCode: 200,
                    errMessage: 'Missing required parameters'
                })
            }
            let clinic = await db.Clinic.findOne({
                where: { id: data.id },
                raw: false
            })
            if (clinic) {
                clinic.name = data.name;
                clinic.address = data.address;
                clinic.descriptionMarkdown = data.descriptionMarkdown;
                clinic.descriptionHTML = data.descriptionHTML;
                if (data.avatar) {
                    clinic.image = data.avatar;
                }
                await clinic.save();
                resolve({
                    errCode: 0,
                    message: 'Update the clinic succeeds!'
                })
            } else {
                resolve({
                    errCode: 1,
                    errMessage: `Clinic's not found!`
                });
            }
